//kulp
//2021
//do

float plot(vec2 p,float x) {
     return smoothstep(x-.02,x,p.y)-
            smoothstep(x,x+.02,p.y);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {

    vec2 p = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;  

    float d = length(p) +.025;

    float a = .5;

    //kulp quartic
    float y = .5*a/sqrt(p.x*p.x+a*a);

    p.y = abs(p.y); 

    float y2 = .001+cos(p.x*p.y*3.)-.5;
    float r = plot(p,y);
    float g = plot(p,y2);

    vec3 c = vec3(.41);  
    c = mix(c,vec3(.05),1.-smoothstep(.48,.5,d));
    //c += mix(c,vec3(1.12),smoothstep(.1,.5,g));
    c += mix(c,vec3(1.,0.,0.),smoothstep(.3,.5,r));

    fragColor = vec4(c,1.);

}
