// Original shaders by lomateron.
// I find it rather pleasing how the core loop for each of the buffers can be distilled into a short one-liner,
// such complex behaviour from quite simple systems!

MAIN {
    vec4 a = Du;
    if(SHADER == 2) { // veinss
        r = sin(a.x*4.+vec4(1,3,5,4))*.25 + sin(a.y*4.+vec4(1,3,2,4))*.25 + .5;
    } else if(SHADER == 3) { // exploding blobs
        r = a.z+a.z*sin(length(a.xy)+vec4(1,2,3,4));
    } else if(SHADER == 4) { // wigli wires
        r = sin(a.x*2.+vec4(1,2,3,4)+0.)*.25 + sin(a.y*2.+vec4(1,2,3,4)+0.)*.25 + .5;
    } else if(SHADER == 5) { // spirals vs ghosts
        r = pow(a.z,.15)*(cos(length(a.xy)*2.+vec4(0,2,4,6)+3.)*.3+.7);
    } else if(SHADER == 6) { // radioactive space balls
        r = sin(pow(a.z,.15)*5.+vec4(1,2,3,4))*.5+.5;
    } else if(SHADER == 7) { // exotic smoke
        r = a.xxyz*.1+sin(pow(a.z,.2)*2.+vec4(1,2,3,4))*.5+.5;
    }
}

#define Dp vec2(dot(nij, Dd.xy), dot(nij, rot90(Dd.xy)))

MAIN {
    r = vec4(0);
    vec4 w = vec4(0);
    if(SHADER == 2) {
        float z = .5*.5 / 3.14; // gaussian normalisation const
        CONV(6) r += z * G(.5 * ij) * vec4(Dp.x, abs(Dp.y), 1,1);
    } else if(SHADER == 3) {
        CONV(8) r += .375 * lij * G(.375 * ij) * vec4(nij * Dd.z, Dp * Di.z);
    } else if(SHADER == 4) {
        CONV(4) r.x += length(Dd.xy);
    } else if(SHADER == 5) {
        w = vec4(.7, .175, .1, 1);
    } else if(SHADER == 6) {
        w = vec4(.66, .25, .1, 1);
    //} else if(SHADER == 7.5) {
    //    w = vec4(2, .5, 1, 1);
    //} else if(SHADER == 8.5) {
    //    CONVO(8) r += vec4(vec2(length(Dd.xy) * sin(dot(ij/8., rot90(Dd))*6.)), dot(nij, rot90(Dd)), 0);
    } else if(SHADER == 7) {
        w = vec4(0,0,1,1);
    //} else if(SHADER == 9) {
    //    CONV(8) r += dot(Di.xy,ij/4.)*G(ij/4.);
    }
    if(w.w != 0.) CONVO(8) r += vec4(nij * Dd.z * cos(w.x * lij) * G(w.y * lij) * w.z, Dp);
}

MAIN {
    r = Du;
    vec4 w = vec4(0);
    if(SHADER == 2) {
        w = vec4(1, 1, .5, 3);
    } else if(SHADER == 3) {
        w = vec4(-.0225 * Du.z, 1, .375, 2);
    } else if(SHADER == 4) {
        w = vec4(-.00675, 1, .75, 0);
    } else if(SHADER == 5 || SHADER == 7) {
        w = vec4(1./8e3, 1, 0, 1);
    //} else if(SHADER == 8.5) {
    //    w = vec4(-1e-6, 0, 1./8., 1);
    //} else if(SHADER == 9) {
    //    w = vec4(-.0015, 1, .5, 0);
    }
    CONVO(8)
        r.xy += w.x * mix(Ai.xy, vec2(1), w.y) * G(w.z * ij)
            * ( w.w == 0. ? ij * Ai.x
              : w.w == 1. ? nij * Ai.z
              : w.w == 2. ? rot90(ij) * Ai.w
              : nij * (Ai.x*.1 + Ad.y*.5));
}

MAIN {
    r = Bu;
    float w = 1.;
    if(SHADER == 1) {
        r = Du;
    } else if(SHADER == 3) {
        r += Au * (Bu.z-.5);
        w = .5;
    } else if(SHADER == 5) {
        r += Au;
        w = 1.5;
    } else if(SHADER == 6) {
        r = Du + Au;
        w = 1./.75;
    //} else if(SHADER == 7.5) {
    //    r += Au;
    } else if(SHADER == 7) {
        r += Au * (Bu.z-.5)*.3;
    //} else if(SHADER == 9) {
    //    r = Du;
    //    w = 1./6. * (sin(Du.z*3.)*2.+4.);
    }
    float s = 0.;
    CONV(8) s += G(w * (r.xy + ij));
    r = vec4(r.xy, recip(s), 0);
}

MAIN {
    r = vec4(0);
    vec4 w = vec4(0);
    /*if(SHADER == 1) {
        CONV(6) r += Di.z * G(Ci.xy+ij) * vec4(Ci.xy+(Ci.xy+ij)*(.8-Di.z),Ci.z,1);
    } else*/ if(SHADER == 2 || SHADER == 4) {
        w = vec4(0,1,0,0);
    } else if(SHADER == 3) {
        w = vec4(1, .5, 0, 0);
    } else if(SHADER == 5) {
        w = vec4(1, 1.5, 0, 0);
    } else if(SHADER == 6) {
        w = vec4(2, 1./.75, 0, 0);
    } else if(SHADER == 7) {
        w = vec4(1,1,0,0);
    //} else if(SHADER == 9) {
    //    CONV(6) r += vec4((Bi * Ci.z * G((Bi.xy-ij)/6. * (sin(Bi.z*3.)*2.+4.))).xyz, 0);
    }

    CONV(8) r += (w.x == 0. ? 1. : w.x == 1. ? Bi.z : Di.z) * G(w.y * (Ci.xy - ij)) * vec4(Ci.xyz, 1);
    r.xy *= recip(r.w);

    int scales[9] = int[](0,22,4,4,8,16,8,8,2);
    float scale = float(scales[SHADER-1]);
    if(iMouse.z>0.) {
        vec2 m = scale*(u-iMouse.xy)/R.y;
        r += vec4(m,0,0)*G(m)*.1;
    }
    if(iFrame < 2) {
        vec2 m = scale * (UV - 0.5);
        //if(SHADER == 9) r = vec4(.1*G(m));
        r = vec4(m,1,1)*G(m);
    }
}
