#define NOTSMOOTH 1
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 lightPos = vec3(0,2,0);
	vec3 lightColor = vec3(1,1,1);
	vec3 pos = vec3(0);
    pos.xy= uv*10.0f-5.0f;
	pos.z = 4.0f;
	vec3 L = normalize(lightPos - pos);
	vec3 n = vec3(0,0,-1);
    #if NOTSMOOTH
    //not smooth result
	fragColor = vec4(0.5f * max(0.0f,dot(n,L)));
    #else
    //smooth version,thans to comments
    fragColor = vec4(0.5f * max(0.0f,dot(n,L))) + texelFetch(iChannel0, ivec2(fragCoord) & 1023, 0) * (1.0 / 256.0);
    #endif
}
