void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 nMouse = iMouse.xy/iResolution.xy;

    // fix aspect ratio
    uv.x *= iResolution.x/iResolution.y; 
    nMouse.x *= iResolution.x/iResolution.y; 

    float mouseProx = distance(uv, nMouse);
	fragColor = vec4(
        abs(sin(0.71*iTime - uv.x) * 8.0 * mouseProx),
        abs(sin(0.6*iTime - uv.y) * 8.0 * mouseProx),
        abs(sin(0.572*iTime)),
        1.0);

}
