#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x)) 
// from wrighter https://www.shadertoy.com/view/WlfyRs
vec3 GetRdSpherical ( vec2 uv ) {
    // polar coords
    uv = vec2(atan(uv.y,uv.x),length(uv));
    uv += 0.5;
    uv.y *= 3.141592;
    // parametrized sphere
    return vec3(cos(uv.y)*cos(uv.x),sin(uv.y),cos(uv.y)*sin(uv.x));
}

uint WangHash ( inout uint seed ) {
	seed = uint( seed ^ uint( 61 ) ) ^ uint( seed >> uint( 16 ) );
	seed *= uint( 9 );
	seed = seed ^ ( seed >> 4 );
	seed *= uint( 0x27d4eb2d );
	seed = seed ^ ( seed >> 15 );
	return seed;
}
float RandomFloat ( inout uint wangState ) { return float( WangHash( wangState ) ) / 4294967296.0; }
vec3 DateBasedColor () {
    uint seed = uint( iDate.x ) + 451u * uint( iDate.y ) + 1984u * uint( iDate.z );
    return vec3( RandomFloat( seed ), RandomFloat( seed ), RandomFloat( seed ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ( fragCoord / iResolution.xy ) - 0.5;
    vec3 dir = GetRdSpherical( uv );
    dir.xy = rot( iDate.a ) * dir.xy;
    dir.yz = rot( iDate.a * 1.618 ) * dir.yz;
    // compute a color
    vec3 base = DateBasedColor();
    vec3 color = vec3( base.r, 0.0, 0.0 ) * abs( dot( dir, vec3( 0.0, 1.0, 0.0 ) ) ) +
                 vec3( 0.0, base.g, base.b ) * sin( 5.0 * dot( dir, vec3( 1.0, 0.0, 0.0 ) ) ) +
                 vec3( 0.0, 0.0, base.b ) * abs( cos( dot( dir, vec3( 0.0, 0.0, 1.0 ) ) * 4.0 ) );
    fragColor = vec4( color, 1.0 );
}
