float steppedLiner(float value, float steps){
    return clamp(round(value * steps) / (steps - 0.0), 0., 1.);
}

float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
float animation(float p){
    return sin(p * 6.283 + iTime * 1.0) * 0.5;
}

float pingPong(float value, float scale){
    return scale - abs( mod(abs(value), (2. * scale)) - scale);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy;

  vec2 position = ( fragCoord.xy / iResolution.xy );
  vec2 scale = uv * 8.0;
  vec2 i_uv = floor(scale);
  vec2 f_uv = fract(scale);
  float color = 1.;
  float noise = 0.;
  noise = (random(i_uv))+animation(0.5);


  float mapped_noise = 0.;

  /*補完処理*/
  mapped_noise = round(noise);
  mapped_noise = (clamp((mapped_noise*2.0)-1.,-1.,1.)); //-1~1

  color= (dot(scale,vec2(mapped_noise,1.)));


  color = pingPong(color,0.5);
  color = step(0.3,color);

  fragColor = vec4(vec3(color),1.);
}
