void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv*2.-1.;
    uv.x *= iResolution.x/iResolution.y;


    uv.x += step(0.1, mod(uv.y, 0.2)) * min(0.,sin(iTime))*0.2;
    uv.y += step(0.1, mod(uv.x, 0.2)) * max(0.,sin(iTime))*0.2;
    uv = fract(uv*5.);

    float a = smoothstep(0.25, 0.3, length(uv-vec2(0.5)));    


    // Output to screen
    fragColor = vec4(0.3, 0.6, sin(iTime), 1.)+vec4(a);
}
