float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
float rand(vec3 co){ return rand(co.xy+rand(co.z)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float vCenter = iResolution.y/2.0;

    float distFromMouse = ( iMouse.x - fragCoord.x );

    float distFromCenterX = (iResolution.x/2.0 - iMouse.x);

    float sinCenter = vCenter + sin( (iTime * distFromCenterX) / -55.0 + fragCoord.x / clamp( 2.0, abs(distFromCenterX)/2.0, 100.0) ) * distFromMouse /4.0;

    float lenFromCenter = abs(sinCenter - fragCoord.y);

    //vec3 color = vec3( clamp(0.0, abs(lenFromCenter / 20.0), 1.0) );
    vec3 color = vec3( 1.0 - lenFromCenter );
    //color.r = distFromMouse/iResolution.y * 2.0;
    //color.g = distFromMouse/iResolution.y * .4;
    color.g = (1.0 - lenFromCenter / 35.0);
        color.r = (1.0 - lenFromCenter / 80.0) + (rand(iTime) * .2);   

    // Output to screen
    fragColor = vec4( color, 1.0);
}
