// modified from SmoothLife by davidar - https://www.shadertoy.com/view/Msy3RD

const float R = 15.;       // space resolution = kernel radius
const float T = 10.;       // time resolution = number of divisions per unit time
const float dt = 1./T;     // time step
const float mu = 0.26;     // growth center
const float sigma = 0.027; // growth width
const vec4  beta = vec4(1.,1./3.,7./12.,0.);   // kernel ring heights
const float betaLen = 3.;  // kernel ring number
const float rho = 0.5;     // kernel center
const float omega = 0.15;  // kernel width
const float noise_base = 0.15;

// species: 4Q5m Pentaquadrium metamorpha [1,2/3,1/3,2/3],0.13,0.007
// species: 3R4s Crucium solidus [1,1/6,5/12],0.32,0.044
// species: 3R4s Crucium solidus [7/12,11/12,1],0.26,0.03  noise 0.16
// species: 3R5f Asterium fluitans [1,0,1],0.32,0.044  noise 0.2
// species: 3R5v Asterium vagus [1,1/2,1],0.27,0.033  noise 0.16
// species: 3B6s Hexabullium solidus [1,1/3,7/12],0.26,0.027  noise 0.15

float bell(float x, float m, float s)
{
    return exp(-(x-m)*(x-m)/s/s/2.);  // bell-shaped curve
}

float getWeight(in float x, in float y)
{
    float r = sqrt(x*x + y*y) / R;
    float Br = r * betaLen;
    float height = beta[int(Br)];
    /*
    if (int(Br)==0) height = beta[0];
    else if (int(Br)==1) height = beta[1];
    else if (int(Br)==2) height = beta[2];
    else if (int(Br)==3) height = beta[3];
    */
    if (r <= 1.)
        return height * bell(mod(Br, 1.), rho, omega);
    else
        return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float sum = 0.;
    float total = 0.;
    for (int x=-int(R); x<=int(R); x++)
    for (int y=-int(R); y<=int(R); y++)
    {
        vec2 txy = mod((fragCoord + vec2(x,y)) / iResolution.xy, 1.);
        float val = texture(iChannel0, txy).r;
        float weight = getWeight(float(x), float(y));
        sum += val * weight;
        total += weight;
    }
    float avg = sum / total;

    float val = texture(iChannel0, uv).r;
    float growth = bell(avg, mu, sigma) * 2. - 1.;
    float c = clamp(val + dt * growth, 0., 1.);

    // draw kernel
    /*/
    float x = (fragCoord.x - iResolution.x/2.) / 10.;
    float y = (fragCoord.y - iResolution.y/2.) / 10.;
    c = getWeight(x, y);
    x = (fragCoord.x - iResolution.x*4./5.);
    y = (fragCoord.y - iResolution.y/2.);
    c += getWeight(x, y);
    /**/

    if (iFrame < 1) // || iMouse.z > 0.
        c = noise_base + noise(fragCoord/R + mod(iDate.w,1.)*100.);
    if (iMouse.z > 0.) {
        float d = length((fragCoord.xy - iMouse.xy) / iResolution.xx);
        if (d <= R/iResolution.x)
        	c = 0.3 + noise(fragCoord/R + mod(iDate.w,1.)*100.);
    }

    fragColor = vec4(c,c,c,1.);
}

// Noise simplex 2D by iq - https://www.shadertoy.com/view/Msf3WH

vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}
