vec3 hash3( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec3 k = n * uvec3(n,n*16807U,n*48271U);
    return vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy - .5;  
     float t = iTime * 0.07;
    // u.y+= fract(t)*3.0 - 1.5;

    u.x -= sin(u.x)*0.95;
    u.y += 0.1 + sin(u.y) * 0.1;

    // u.y += sin(u.x*4.0)*0.5;
    uvec2 p = uvec2(fragCoord);
	vec3 h = hash3( p.x + 1920U*p.y + (1920U*1080U) );
    float k = length(u * 1.5) - h.r * 0.0037;

    fragColor = vec4(k);
}
