void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float sdf = length(uv) - 1.03125;

    vec4 col = vec4(0.152, 0.284, 0.768, 1.0) - step(0.0, sdf); // water base color

    col *= vec4(
        0.5 * cos((col.x * -sdf * 6.283 * 5.0) + iTime * 5.0) + 0.5,
        0.5 * cos((col.y * -sdf * 6.283 * 5.0) + iTime * 5.0) + 0.5,
        0.5 * cos((col.z * -sdf * 6.283 * 5.0) + iTime * 5.0) + 0.5,
        0.5 * cos((col.w * -sdf * 6.283 * 5.0) + iTime * 5.0) + 0.5
    ); // waves

    col *= -clamp(sdf, -1.0, 0.0);

    fragColor = col + vec4(0.1, 0.2, 0.4, 1); // background color
}
