void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy - iResolution.xy) / iResolution.y;

    float sun = 0.05 / length(ms.xy - uv.xy) + 0.02;

    fragColor = vec4(vec3(sun / 0.477, sun + 0.5, sun + 0.8),1.0);
}
