#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 36.*4.*8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    float p1 = 1.;
    for(int i=0;i<1;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 18; k++){
            //uv.x += ceil(uv.x+float(k))/3.; 

            uv += (t2.yx);
            //uv -= ceil(t2.x*4.)/4.; //mosaic pattern

            uv /= scale;
            uv += vec2(1.); //this also makes an interesting pattern
            t2 = -p1*triangle_wave(uv-.5,1.5);
            t3 = p1*triangle_wave(uv.yx,1.5);
            //t2 = abs(t2-.5);
            //t3 = abs(t3-.5);
            uv.yx = (t2+t3);
            p1 *= -1.;
            col.x = max(uv.y-uv.x-col.x,col.x*2.5);
        //col.x = min(uv.y-uv.x+col.x,col.x*2.);

        //uv.x *= -1.;

        //col.x = abs(col.x-.5);
        col = abs(col.yzx-vec3(1.5-col.x))/2.;
        //uv.x += .5;
        //col = abs(col.yzx+vec3(col.x)*2.)/3.;
        }


        uv.y *= -1.;
    }
    fragColor = vec4(col*1.5,1.0);
}
