void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float thickness = abs(sin(iTime * .5) * 10.);
    float l = abs(cos(iTime * .1) * 200.);
    float bias = (abs(cos(iTime * .35) + 1.)/2.) * 0.5;

    float m = ceil(abs(clamp(pow(sin(uv.x*l),(1. / thickness)), .0, 1.) - 1.) - bias);
    float c = ceil(abs(clamp(pow(sin(uv.y*l),(1. / thickness)), .0, 1.) - 1.) - bias);

    vec3 glow = vec3(5., 0., 0.);

    vec3 col = texture(iChannel0, uv).xyz;

    col = mix(col, glow, clamp(m + c, 0., 1.));

    fragColor = vec4(col, 1.0);
}
