vec3 cutSquare(vec2 uv, vec3 col, vec2 pos, vec2 size) {
  if (uv.x > pos.x && uv.x < size.x + pos.x) {} else {
    col = vec3(.0);
  }

  if (uv.y > pos.y && uv.y < size.y + pos.y) {} else {
    col = vec3(.0);
  }
  return col;
}

vec3 tex2D(sampler2D _tex, vec2 _p) {
  vec3 col = texture(_tex, _p).xyz;
  if (0.5 < abs(_p.x - 0.5)) {
    col = vec3(0.1);
  }
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  vec2 uv = fragCoord / iResolution.x;
  vec4 m = iMouse / iResolution.x;
  vec3 col = tex2D(iChannel0, uv);
  vec3 cola;
  if (m.z > 0.0) 
  {
    for (int i = 1; i != 11; i++) {
      cola += cutSquare(uv, col, vec2(0.1 * float(i) - 0.1, mod(m.y + 0.2, 1.) - 0.5 + (0.2 + sin(0.2 + m.y * float(i)) / 10.)), vec2(0.1));
    }
  } else {
    for (int i = 1; i != 11; i++) {
      cola += cutSquare(uv, col, vec2(0.1 * float(i) - 0.1, mod(iTime, 1.) - 0.5 + (0.2 + sin(iTime * float(i)) / 10.)), vec2(0.1));
    }
  }
  fragColor = vec4(cola, 1.0);
}
