void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; 
    vec4 texColor = texture(iChannel0,xy);

    float average = (texColor.r+texColor.g+texColor.b)/5.0;  
    vec4 grayScale = vec4(texColor.r - average,average,average,1.0); 
    fragColor =  grayScale;
}
