vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - (sin(iTime+(uv.x*0.5*uv.y))*rad);
	float t = clamp(d, 0.2, 0.8);
	return vec4(color, 1.-t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.33 * iResolution.x;


	vec4 bg = vec4(vec3(0.2), 1.0);
	vec4 layer2 = circle(uv, center, radius, vec3(1.0));

	fragColor = mix(bg, layer2, layer2.a);

}
