void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    //channel offset
    vec4 tex0 = texture(iChannel0, uv);
    vec3 tex0a = vec3(0.);

    tex0a.r = texture(iChannel0, vec2(uv.x/(1.0055+0.015*sin(ceil(iTime*13.))), uv.y)).r;
    tex0a.b = texture(iChannel0, vec2(uv.x/(1.0055+0.015*sin(iTime*3.)), uv.y)).b;
    tex0a.g = texture(iChannel0, vec2(uv.x/(1.0075+0.02*(cos(iTime))), uv.y/1.0098+0.02*(cos(floor(iTime*5.))))).g;

    //tex0.r = texture(iChannel0, vec2(uv.x/(1.0055+0.005*sin(iTime*3.)), uv.y)).r;
    //tex0.g = texture(iChannel0, vec2(uv.x/(1.0045-0.02*(cos(iTime))), uv.y)).g;

    vec3 lighterRB = vec3(max(tex0.r, tex0a.r),tex0.g, max(tex0.b, tex0a.b) );
    //vec3 lighterRB = tex0.rgb;
    lighterRB = vec3(lighterRB.r, min(lighterRB.g, tex0a.g), lighterRB.b) ;



    // Output to screen
    fragColor = tex0;
    fragColor = vec4(lighterRB, 1.);
}
