void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.yy;

    uv.xy -= iMouse.xy / iResolution.xy;    

    float tile_size = 1.0 / 8.0;

    float edge = tile_size / 24.0;

    uv = (mod(uv, tile_size) - mod(uv - edge, tile_size) - edge) * (1.0 / tile_size);
    vec4 grid_frag = vec4(1.0 - length(uv) * 0.5); // 0.9 for the face of the tile, 0.5 for the edge

    // Normalized pixel coordinates (from 0 to 1)
    uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.75 + 0.50 * cos(
        iTime + uv.xyx + vec3(2,0,0)
    );

    // Output to screen
    fragColor = vec4(col,1.0) - grid_frag;

}
