float circle(vec2 crd){
     float r = 10.;
     return (length (crd)-10.);
     }

void mainImage(out vec4 O, vec2 U) {
    mat2  C = mat2( sin(.4*iTime+vec4(0,33,11,0)) ); //rot matrix
    vec3  D = vec3( (U-.5*iResolution.xy)/2e3, -1),  // coord field 
          B, R;                                      // Buffer, Ray                                        
    O -=O;
    for (R.z = 50.; O.r<.85 ; O+=.03){
       B = R;  B.yz *= C; B.xz *= C;                 // rotation
       B = abs(B);
       float t= max(max(B.x-3.,B.z+2.),B.y)-4.;
       R += t*D;                                     //ray increment 
       if (t < .1 )  {O.r +=.6; break;}       
    }  
    if (U.y > 50.) O.r -=pow(length(O) -.6, 9.);
    else {O -=O;
          O.r += circle(vec2(iMouse.x,iMouse.y));
          }
}
