float p;
#define AAstep(edge, x) smoothstep(edge - p, edge + p, x)

float getCross(vec2 uv, float size, float len)
{
    uv = abs(uv);
    vec2 line = step(uv.yx * 2., step(uv,vec2(len)) * size);
    return max(line.x, line.y);
}

vec3 georgianFlag(vec2 uv)
{
    float rot = uv.x * 6. - iTime * 2.5 + uv.y * 2.;
    uv.y += sin(rot) * 0.05;

    if(abs(uv.y) > .5 || abs(uv.x) > 0.75) 
        return vec3(0);

    float mainCross = getCross(uv, 0.175, .75);
    float crosses = getCross(-abs(uv) + vec2( 0.5,  0.3), 0.07, 0.15);
    float flag = mainCross + crosses;

    float shade = .9 + cos(rot) * .2;

    const vec3 WHITE = vec3(1);
    const vec3 RED = vec3(0.85, 0.16, 0.2); 
    vec3 col = mix(WHITE, RED, flag) * shade; 

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord - R.xy * 0.5) / R.y; 
    p = length(fwidth(fragCoord / R.xy))*8.;
    fragColor = vec4(georgianFlag(uv * 1.25),1.0);
}
