void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvs = fragCoord/iResolution.xy;
    float im = impostorMusic(iTime);


    vec2 uv = uvs;
    uv -= vec2(0.5, 0.5);

    vec2 dst = uv - vec2(0.0, 0.0);

    float len = length(dst);
    dst = normalize(dst);

    dst = dst * pow(len, 1.0 + 1.2 * im);
    uv = dst;

    uv += vec2(0.5, 0.5);

    vec4 uvt = texture(iChannel0, uv);

    vec2 uvd = uvs;
    uvd -= vec2(0.5, 0.5);

    dst = uvd - vec2(0.0, 0.0);

    len = length(dst);
    dst = normalize(dst);

    dst = dst * pow(len, 1.0 - 0.4 * im);
    uvd = dst;

    uvd += vec2(0.5, 0.5);

    vec4 uvdt = texture(iChannel0, uvd);

    // Output to screen
    fragColor = (uvdt * 0.15 + uvt * 0.85);

    float cnorm = fragColor.r + fragColor.g + fragColor.b;

    im = clamp(im * 20.0 * im, 0.0, 1.0);

    fragColor = fragColor * (1. - im);
    fragColor.g = fragColor.g + (0.5 + 0.5 * sin(cnorm * 5.0)) * im * 2.0 * cnorm;
}

