float PI = 3.14159265358979323846;

float saw(float x) {
    return asin(sin(x)) * sign(cos(x));
}


vec2 synthSaw(float time, float amp, float freq, float timeOffset, float decay, float timeLimit) {

    time = time - timeOffset;
    if (time < 0. || time > timeLimit) return vec2(0.0);

    return amp * vec2( saw(2.0 * PI * freq * time)*exp(-decay * time) );
}

float impostorSaw(float time, float amp, float freq, float timeOffset, float decay, float timeLimit, float dummy) {

    time = time - timeOffset;
    if (time < 0. || time > timeLimit) return 0.0;

    return amp * exp(-decay * time);

}

vec2 synthSuSaw(float time, float amp, float freq, float timeOffset, float decay, float timeLimit, float distr) {

    distr = 1.0 + distr;

    float d = distr;
    float d2 = d * d;
    float d3 = d2 * d;

    float a1, a2, a3, a4, a5, a6, a7;

    a1 = 0.1;
    a2 = 0.2;
    a3 = 0.3;
    a4 = 1.0;
    a5 = 0.3;
    a6 = 0.2;
    a7 = 0.1;

    vec2 sum = a1 * synthSaw(time, amp, freq / d3, timeOffset, decay, timeLimit) +
        a2 * synthSaw(time, amp, freq / d2, timeOffset, decay, timeLimit) +
        a3 * synthSaw(time, amp, freq / d, timeOffset, decay, timeLimit) +
        a4 * synthSaw(time, amp, freq, timeOffset, decay, timeLimit) +
        a3 * synthSaw(time, amp, freq * d, timeOffset, decay, timeLimit) +
        a2 * synthSaw(time, amp, freq * d2, timeOffset, decay, timeLimit) +
        a1 * synthSaw(time, amp, freq * d3, timeOffset, decay, timeLimit);

    sum = sum / (a1 + a2 + a3 + a4 + a5 + a6 + a7);

    return sum;
}

