void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // setzt Nullpunkt(0/0) in die Mitte
    uv.x *= iResolution.x/iResolution.y;  // macht, dass trotz des rechteckigen Bildschirms der Kreis rund und nicht elliptisch ist.

    float r = 0.3;  // Radius vom Kreis -> Grösse
    float d = length(uv);

    float c = step( r, d); // Übergang am Kreisrand zwischen r und r-0.015
                                          // für c < r: return 0 -> weiss
                                          // für c >= r: return 1 -> schwarz
    // if (d<.3) c = 1.; else c = 0.; (andere Variente für Kreis)

    fragColor = vec4(vec3(c),1.0); //Farbe 
}
