void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = fragCoord.xy / iResolution.xy;

  float horizon = 0.35;
  float checker_size = (uv.y - horizon) * 200.0;
  float pattern_x = floor((fragCoord.x - (iResolution.x / 2.0)) / checker_size);
  float pattern_y = floor(fragCoord.y / checker_size);
  vec2 Pos = vec2(pattern_x, pattern_y);
  float pattern_mask = mod(Pos.x + mod(Pos.y, 2.0), 2.0);

  float alpha = 0.5;
  vec4 red = vec4(0.5, 0.0, 0.0, alpha);
  vec4 green = vec4(0, 0.5, 0.0, alpha);
  vec4 blue = vec4(0.0, 0.0, 0.5, alpha);
  vec4 black = vec4(0.0, 0.0, 0.0, alpha);
  vec4 white = vec4(1.0, 1.0, 1.0, alpha);

  fragColor = blue;
  float horizon_y = uv.y - horizon;
  if (horizon_y < 0.0)
  {
    fragColor = red;
    if (pattern_mask > 0.0)
      fragColor = green;

    float road_width = horizon - uv.y;
    float road_left = 0.5 - road_width;
    float road_right = 0.5 + road_width;
    if (uv.x > road_left && uv.x < road_right)
    {
      fragColor = black;
    }

    float marker_width = road_width / 25.0;
    float marker_left = 0.5 - marker_width;
    float marker_right = 0.5 + marker_width;
    float marker_y = floor((fragCoord.y / checker_size) - (iTime * 3.0));
    if (uv.x > marker_left && uv.x < marker_right)
    {
      if (mod((marker_y), 2.0) > 0.0)
        fragColor = white;
    }
  }
}
