// Other ideas:
// https://www.shadertoy.com/view/st2BW3
// https://www.shadertoy.com/view/ft2fDc
// https://www.shadertoy.com/view/sl2BW3

float h21 (vec2 a, float rep) {
    a = mod(a, rep);
    return fract(sin(dot(a, vec2(12.9898, 78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sc = 40.;
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    // threshold
    float thr = 0.5;
    float rep = 301.;
    float hl = step( h21(ipos - vec2(0.5,0), rep), thr );
    float hr = step( h21(ipos + vec2(0.5,0), rep), thr );
    float hb = step( h21(ipos - vec2(0,0.5), rep), thr );
    float ht = step( h21(ipos + vec2(0,0.5), rep), thr );

    float v =  hl * step(fpos.x, -0.25);
    v = max(v, hr * step(0.25, fpos.x));
    v = max(v, hb * step(fpos.y, -0.25));
    v = max(v, ht * step(0.25, fpos.y));

    fragColor = vec4(v,v,v,1.0);
}
