void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord.xy / iResolution.xy; //Соединяем это в одну строку

    vec4 texColor = texture(iChannel0,xy); //получаем от iChannel0 пиксель в координате xy

    float gray = texColor.r * 0.299 + texColor.b * 0.114 + texColor.g * 0.587;
    texColor.r = gray;
    texColor.b = gray; //эти 4 строки кода делают изображение черно-белым
    texColor.g = gray;

    texColor.r *= abs(sin(iTime));
    texColor.b *= abs(cos(iTime)); // эти 3 строки кода добавляют движение градиента
    texColor.g *= abs(sin(iTime) * cos(iTime));



    //float gray = texColor.r * 0.299 + texColor.g * 0.587 + texColor.b * 0.114;
    //texColor.r = gray;
    //texColor.g = gray; //эти 4 строки кода делают изображение черно-белым
    //texColor.b = gray;



    //texColor.g = xy.x;
    //texColor.a = xy.y;

    fragColor = texColor; //Присваиваем экранному пикселю этот цвет
}
