void mainImage(out vec4 col, in vec2 coord) {
    vec2 xy = (coord - iResolution.xy / 2.) / min(iResolution.x, iResolution.y);
    xy *= exp(log(4.)*mod(iTime, 1.));
    xy *= mat2(
        cos(.25*iTime), -sin(.75*iTime),
        sin(.5*iTime), cos(.25*iTime)
    );
    for (int i = 0; i < 8 && min(abs(xy.x), abs(xy.y)) < 1.; i++) {
        xy *= 2.;
    }
    for (int i = 0; i < 8 && min(abs(xy.x), abs(xy.y)) > 1.5+0.5*cos(0.6*iTime); i++) {
        xy /= 2.;
    }
    col = vec4(
        abs(xy) - 1.,
        0.5+0.5*sin(0.1*iTime),
        1.
    );
}
