float PI = 3.14,
      e = 2.71,
      R = .8,
      n = 10.,
      m = 10.,
      z = 2.;
vec2 c = vec2(.0, 0.);

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = z * ( 2. * u - Res ) / Res.y;   
    O = vec4(0.);
    float A = 0.2;
    float B = .01;
    float r = distance(U, z * c);

    for(float i = 0.; i < abs(sin(iTime/4. + PI/2.)) * m; i++) {
        for(float j = 0.; j < abs(sin(iTime/4. + PI/2.)) * n; j++) {
            float Rd = R * i * j;
            float a = ((j)/(n)) * 2. * PI + iTime;
            r = distance(U - z * c, Rd * vec2(-sin(a), cos(a)));
            O += vec4(min(exp((-r + Rd)/.02), 1.)) * B;
        }
    }
}
