#define PI 3.14159265

mat2 rot(in float a)
{
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec4 last = texture(iChannel0, fragCoord/iResolution.xy);
    //draw a stupid point somewhere
    vec2 seed = vec2(0);
    vec4 new = vec4(smoothstep(1.5/iResolution.y, 0., length(uv - seed)));
    //4 copies
    //1 - squished to line in center
    vec2 cuv = uv;
    cuv *= vec2(40., 3. );
    cuv += vec2(0, .75);
    new += texture(iChannel0, cuv);

    //2 - rotated left and shifted left
    cuv = uv;
    cuv *= rot(-PI/2.0);
    cuv *= 2.8;
    cuv += .5;
    cuv += vec2(0.8, -.5);
    new += texture(iChannel0, cuv);

    //3 - shfifted up and rotated a bit right, scaled down
    cuv = uv;
    cuv *= rot(.1 + sin(.1*iTime));
    cuv *= 1.2;
    cuv += .5;
    cuv += vec2(-0.04, -.05);
    new += texture(iChannel0, cuv);

    //4 - rotated right and shifted right
    cuv = uv;
    cuv *= rot(PI/2.0);
    cuv *= 2.8;
    cuv += .5;
    cuv += vec2(-1., -.3);
    new += texture(iChannel0, cuv);

    fragColor = clamp(.4*last + new, 0., 1.);
}
