float PI = 3.14;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    vec2 rotU = rotZ(U, iTime/10. - PI/4.);
    O = vec4(min(.01/abs(U.y - iTime/10. * U.x), 1.)) + 0.5 * vec4(min(.01/abs(rotU.y - rotU.x), 1.));;
}
