//<300 chars playlist: https://www.shadertoy.com/playlist/fXlGDN
//Tweet: https://twitter.com/XorDev/status/1511097804294340610
//Twigl: https://t.co/rafF5apeRY

void mainImage( out vec4 O, vec2 p)
{
    //Y-resolution for scaling
    vec2 r = iResolution.yy/6.,
    //Cell coordinates
    c = ceil(p=p/r+iTime),
    //Signed vec2
    s = sign(texture(iChannel0,c/2e2).rg-.5),
    //fractional-cell coordinates (-0.5 to +0.5)
    f = (c-p-.5)*s,
    //Truchet distance * checkerboard * sign flips
    i = (length(f+.5-ceil(f.x+f.y))-.5)*(mod(c.x+c.y,2.)-.5)*s*s.y;
    //Color and shading
    O = .5+cos((p+i+i).x+vec4(6,O-O)) * clamp(r*i,-.5,.5).x;
}

