#define MAX_STEPS 128
#define MAX_DISTANCE 100.0
#define SURF_DISTANCE 0.001
#define SMOOTH 0.65

vec3 light_pos = vec3(0,5,0);

float smoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sphereSDF(vec3 pos, vec3 sphere, float radius){
    float a = 10.0;
    float b = 0.05;
    float d = sin(a * pos.x) * sin(a * pos.y) * sin(a * pos.z) * b;
    return (length(pos-sphere)-radius) + d;
}

float spheres(vec3 ray_origin){
    float sphere = sphereSDF(ray_origin, vec3(0,1.0+(cos(iTime)),5), 0.5);
    float sphere2 = sphereSDF(ray_origin, vec3(1.5,1.0+(sin(iTime)),5), 0.5);
    float sphere3 = sphereSDF(ray_origin, vec3(-1.5,1.0+(sin(iTime)),5), 0.5);
    float smooth1 = smoothUnion(sphere,sphere2,SMOOTH);
    float smooth2 = smoothUnion(sphere,sphere3,SMOOTH);
    float smooth3 = smoothUnion(sphere2,sphere3,SMOOTH);
    float min1 = smoothUnion(smooth1,smooth2,SMOOTH);
    float min2 = smoothUnion(min1,smooth3,SMOOTH);
    return min2;
}

float sceneSDF(vec3 ray_origin){
    float plane_distance = ray_origin.y;
    //float sphere = sphereSDF(ray_origin, vec3(0,1.0+(sin(iTime)),5), 1.0);
    //return min(plane_distance,sphere);
    float sphere = spheres(ray_origin);
    return smoothUnion(plane_distance, sphere, SMOOTH);
}

float getDistance(vec3 ray_origin, vec3 ray_direction){
    float distance_from_origin = 0.0;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ray_origin + ray_direction*distance_from_origin;
        float distance_to_scene = sceneSDF(p);
        distance_from_origin += distance_to_scene;

        if(distance_from_origin>MAX_DISTANCE || distance_to_scene<SURF_DISTANCE){
            break;
        }
    }

    return distance_from_origin;
}

vec3 getNormal(vec3 pos)
{
    float distance = sceneSDF(pos);
    vec2 e = vec2(0.01,0);
    vec3 n = distance - vec3(
        sceneSDF(pos-e.xyy),
        sceneSDF(pos-e.yxy),
        sceneSDF(pos-e.yyx)
    );
    return normalize(n);
}

bool isInShadow(vec3 pos){
    vec3 light_direction = normalize(pos-light_pos);
    float distance_to_light = distance(pos,light_pos);

    float distance = getDistance(light_pos, light_direction);

    if(distance >= distance_to_light-0.005){
        return false;
    }

    return true;
}

float getLightLevel(vec3 pos)
{

    //light_pos.xz += vec2(sin(iTime)*4.0,cos(iTime)*4.0);

    vec3 to_light = normalize(light_pos-pos);
    vec3 surface_normal = getNormal(pos);

    return dot(to_light, surface_normal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-(0.5*iResolution.xy))/iResolution.xy;

    // Camera
    vec3 camera_origin = vec3(0,1,0);
    vec3 camera_direction = normalize(vec3(uv.x,uv.y,1));

    float distance = getDistance(camera_origin,camera_direction);

    vec3 distant_point = camera_origin + camera_direction*distance;

    vec3 col = vec3(1.0,1.0,1.0);

    col = vec3(getNormal(distant_point));

    //col = vec3(distance/8.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
