#define res iResolution
#define ft float

ft hm(vec2 uv, vec2 m){
    ft a = dot(uv,uv);
    ft b = sin(.6*iTime+uv.x/a/m.x)-sin(uv.y/a/m.y);
return abs(b*1.4);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-res.xy)/res.y;
    vec2 m = vec2(2.3);
    vec2 mouse = iMouse.y < 1.? vec2(.5): iMouse.xy/res.xy;
    ft a = hm(uv, m);
    for(ft i; i < 6.; i++){
    uv = abs(uv/hm(uv,m+i*.2)-.4*mouse);
    }
    fragColor = vec4(2.*smoothstep(.1,3.,1.-1./uv.xyy),1.0);
}
