void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0);

    vec2 s = 1.5 * ratio;
    vec2 c2 = (texture(iChannel0, uv).xy*2.0 - 1.0)*s;
    vec2 c3 = (iMouse.xy / iResolution.xy *2.0 - 1.0)*s;
    vec2 c = (uv*2.0 - 1.0)*s;

    vec2 z = c;
    vec2 z2;


    // enable for bonus algorithm
    #if 0
    z = abs(z);
    z2.x = z.x*z.x - z.y*z.y;
    z2.y = z.x*z.y*2.0;
    z2 += c3;
    z = z2;
    #endif


    z2.x = z.x*z.x - z.y*z.y;
    z2.y = z.x*z.y*2.0;
    z2 += c3;



    vec2 uv2 = (z2/s + 1.0)/2.0;
    vec4 t = texture(iChannel0, uv2);

    fragColor = max(t*0.99, smoothstep(0.1, 0.0, dot(c, c))); // vec4(0.0,0.0,1.0,1.0);
}
