float wave(vec2 uv, vec2 freqCoord){
    float f = max(texture(iChannel0, freqCoord).r, 0.);
    float f2 = (sin(uv.x+sin(iTime*100.)*100.*f)*0.1-0.5)*(cos(iTime*100.*f)*0.1+0.5);
    return f == 0. ? -0.25 : f2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv *= 9.;

    vec3 col = texture(iChannel1, fragCoord/iResolution.xy+0.1).rgb 
                 * step(length(vec2(uv.x-8., uv.y-4.25)), 3.);
    vec3 col2 = vec3(0.);

    float f = 0.;
    f += step(0.9, smoothstep(6.5, abs(uv.y + wave(uv, vec2(0.15))), 6.56));
    f += step(0.9, smoothstep(5.5, abs(uv.y + wave(uv, vec2(0.17))), 5.55));
    f += step(0.9, smoothstep(4.5, abs(uv.y + wave(uv, vec2(0.18))), 4.54));
    f += step(0.9, smoothstep(3.5, abs(uv.y + wave(uv, vec2(0.20))), 3.53));
    f += step(0.9, smoothstep(2.5, abs(uv.y + wave(uv, vec2(0.21))), 2.52));
    f += step(0.9, smoothstep(1.5, abs(uv.y + wave(uv, vec2(0.22))), 1.51));
    f *= step(0.001, col.r);
    col2.r = f;
    col = mix(col, col2, f);

fragColor.rgb = col;
}
