float R=100.0; float R2=100.0;
float SPEED=200.0; float SPEED2=-200.0; 
float pi = 3.14159265358979323846264338327950288;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord-iResolution.xy/2.0;float X=c.x;float Y = c.y;
    float T = iTime; float O = pow(fragCoord.x,2.0)+pow(fragCoord.y,2.0)+T*100.0;
    float W1 = 5.0;
    float W2 = 10.0;
    float W3 = 20.0;
    float Z = abs(cos((O)/pi/W1)-sin((O)/pi/W1));
    float ZX = abs(cos((O)/pi/W2)-sin((O)/pi/W2));
    float ZY = abs(cos((O)/pi/W3)-sin((O)/pi/W3));
    fragColor = vec4(
        ZX,
        abs(ZX-ZY),
        ZY,
        0
    );

    float dx = 0.0;float dy=0.0;
    float tx = (T*SPEED)+0.002;float ty = (T*SPEED)+0.002;
    if (mod(tx,iResolution.x*2.0) > iResolution.x) tx = -tx;
    if (mod(ty,iResolution.y*2.0) > iResolution.y) ty = -ty;
    /* float dx1 = 0.0; float dy1=0.0;
    float tx1 = (T*200.0)+iResolution.x; float ty1 = T*200.0+iResolution.y;
    if (mod(tx1,iResolution.x*2.0) > iResolution.x) tx1 = -tx1;
    if (mod(ty1,iResolution.y*2.0) > iResolution.y) ty1 = -ty1; */
    float dx2 = 0.0; float dy2=0.0;T=T+777.0;
    float tx2 = (T*SPEED2)-iResolution.x/2.0+0.001; float ty2 = 0.001+T*SPEED2-iResolution.y/2.0;
    if (mod(tx2,iResolution.x*2.0) > iResolution.x) tx2 = -tx2;
    if (mod(ty2,iResolution.y*2.0) > iResolution.y) ty2 = -ty2;

    dx=abs(mod(tx, iResolution.x))-iResolution.x/2.0;
    dx*=(iResolution.x-R*2.0)/iResolution.x;
    dy=abs(mod(ty, iResolution.y))-iResolution.y/2.0;
    dy*=(iResolution.y-R*2.0)/iResolution.y;
    /* dx1=abs(mod(tx1, iResolution.x))-iResolution.x/2.0;
    dx1*=(iResolution.x-200.0)/iResolution.x;
    dy1=abs(mod(ty1, iResolution.y))-iResolution.y/2.0;
    dy1*=(iResolution.y-200.0)/iResolution.y; */
    dx2=abs(mod(tx2, iResolution.x))-iResolution.x/2.0;
    dx2*=(iResolution.x-2.0*R2)/iResolution.x;
    dy2=abs(mod(ty2, iResolution.y))-iResolution.y/2.0;
    dy2*=(iResolution.y-2.0*R2)/iResolution.y;

    if((X-dx)*(X-dx)+(Y-dy)*(Y-dy)<=R*R) {
         fragColor-=vec4(Z)/3.0;
    } vec4 fc = fragColor;
    fc = fragColor;
    if((X-dx2)*(X-dx2)+(Y-dy2)*(Y-dy2)<=R2*R2) {
         fragColor+=vec4(Z)/3.0;
    }

    int SX=(int(iResolution.x)/DS-18)/2;
    int SY=(int(iResolution.y)/DS-7)/2;

    float H=iDate.w/3600.0;
    float M=mod(iDate.w/60.0, 60.0);
    int hh=int(H/10.0); int h=int(mod(H,10.0));
    int mm=int(M/10.0); int m=int(mod(M,10.0));
    ivec2 p = ivec2(fragCoord)/DS;
    if(p.x>=SX+1&&p.x<=SX+17&&p.y>=SY+1&&p.y<=SY+5) {
        if(DRAW(hh,SX,SY,p)) fragColor*=vec4(1.2,0.2,0.3,1);
        if(DRAW(h,SX+=4,SY,p)) fragColor*=vec4(1.2,0.2,0.3,1);
        if(DRAW(-1,SX+=3,SY,p)) fragColor*=vec4(1.2,0.2,0.3,1);
        if(DRAW(mm,SX+=3,SY,p)) fragColor*=vec4(1.2,0.2,0.3,1);
        if(DRAW(m,SX+=4,SY,p)) fragColor*=vec4(1.2,0.2,0.3,1);
    }
}
