mat2 rotate(float a) {return mat2( cos(a), sin(-a), sin(a), cos(a)); }

// Ring/outline fuction by darch.dk (25-04-2022)
float ring (float dF, float radius, float stroke) {
    return step(radius-stroke,dF)*step(dF,radius+stroke); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*0.5) /iResolution.y;

    vec3 col = vec3(0);
    vec2 pos = fragCoord/iResolution.xy;

    float res = (2.+sin(-iTime*0.1))*10. ; 

    pos *= rotate(iTime*0.01);
    col.r = abs(fract(sin(iTime*0.1)*0.2+pos.x * res));
    pos *= rotate(-iTime*0.2);
    col.g = fract(cos(iTime*0.5)*0.3+pos.g * res);

    col *= smoothstep(0.1,0.9,col);

    float d = length(uv);
    float r = 0.12 + cos(iTime*0.5)*0.15;
    float s = 0.1+sin(iTime*0.8)*0.5; 
    col.b = ring(d, r, s);
    col.b = smoothstep(0.45,sin(iTime*0.5),col.b);


    // Duotone
    col = vec3(col.r+col.g+col.b)/3. ;
    col = smoothstep(0.41,0.39, col);

    // mixing colors
    vec3 c1 = vec3(0.9, uv.y, sin(-iTime*0.94566));
    vec3 c2 = vec3(uv.x, cos(iTime*1.143242), 0.9);
    col = mix(c1,c2, col);


    // Output to screen
    fragColor = vec4(col,1.0);
}
