void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float x=uv.x;
    float y=uv.y;    
    x+=x*.3*sin(iTime*.5);


    float t=x*7.+2.*y-2.*iTime;
    uv.y+=y*x*sin(t*.5)*.04;
    uv.x+=y*x*sin(t)*.01;

    float t2=x*7.-1.*y-2.*iTime;
    uv.y+=y*x*cos(t2*4.)*.01;
    uv.x+=y*x*cos(t2*4.)*.01;

    //division line for colors:
    float w=fwidth(uv.y);
    float m= smoothstep(.5-w,.5+ w, uv.y);

    //colors of the flag:
    vec3 col = vec3(1,.9,0);
    vec3 blue= vec3(.1,.3,1);

    vec3 sky= vec3(.2,.6,7.);
    vec3 bg =vec3(1,0,0);

    col= mix(col, blue, m);
    col*=mix(1.,.9+cos(t+sin((-x-y)*2.))*.3,uv.x);
    col+=-x*y*sin(t2*2.+cos((-2.*x+y)*2.)*3.)*.2;

    //flag bounds:
    col*=smoothstep(1.0,.99, uv.y);
    col*=smoothstep(-.01,0., uv.y);
    col*=smoothstep(1.0,.99, uv.x);
    col*=smoothstep(-.01,-.0, uv.x);

    //sky:
    col+=smoothstep(.99,1.0, uv.y)*sky;
    col=max(col,smoothstep(.0,-.01, uv.y)*sky);
    col=max(col,smoothstep(.99,1.0, uv.x)*sky);
    col=max(col,smoothstep(.0,-.01, uv.x)*sky);




    // Output to screen
    fragColor = vec4(col,1.0);
}
