void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

float brushSize = 300.0;

vec4 rgb(float r, float g, float b) {
    return vec4(r / 255., g / 255., b / 255., 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;

    float modeIndex = 0.0;

    vec2 point = fragCoord * 1.0002;

    if (iTime < 0.5) {
        fragColor = rgb(140. / 6., 124. / 6., 79. / 6.);
        return;
    }

	fragColor = texture(iChannel0, point.xy / iResolution.xy);

    float n = 2.;
    float d = length(point.xy-iMouse.xy)* n;

    float a = 1.0-min(d / brushSize, 1.0);

    if (iMouse.w > 0.5) {
        fragColor += rgb(0.0, 240.0, 0.0) * a;
        return;
    } else {
        fragColor -= vec4(.05 * 1.0 - point.x / iResolution.x,.0,.5 * point.x / iResolution.x,0.2) * (0.0 - a);
        return;
    }
}
