    float Circle(float x, float y, float r)
    {
        return x*x + y*y - r;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 p = 1.0 - 2.0 * fragCoord/iResolution.xy;
    float x = p.x * iResolution.x/iResolution.y;
    float y = p.y;    
     // body 
     float z = Circle(x, y-0.25, 0.25);
      // smile
      z *= Circle(x*0.9, y-0.25, 0.05); 
      z = min(z, Circle(x*0.9, y-0.2, 0.05)); 
       // ears
       z = min(z, Circle(x*2.0 +0.5, y+0.4, 0.1));  
       z = min(z, Circle(x*2.0 -0.5, y+0.4, 0.1));  
        // arms
        z = min(z, Circle(x-0.5, y-0.2, 0.02));  
        z = min(z, Circle(x+0.5, y-0.2, 0.02));  
         // legs
         z = min(z, Circle(x-0.25, y-0.7, 0.02));  
         z = min(z, Circle(x+0.25, y-0.7, 0.02));  
          // eyes           
          z *= Circle(x-0.15, y-0.1, 0.01);  
          z *= Circle(x+0.15, y-0.1, 0.01);  
          z /= Circle(x-0.15, y-0.1, 0.001);  
          z /= Circle(x+0.15, y-0.1, 0.001);  
          z *= Circle(x-0.15, y-0.1, 0.005);  
          z *= Circle(x+0.15, y-0.1, 0.005); 
          z /= Circle(x-0.15, y-0.1, 0.0001);  
          z /= Circle(x+0.15, y-0.1, 0.0001);        
           // nose      
           z *= Circle(x, y/0.5-0.4, 0.004);       

            float f = 0.01/z;
             vec3 col = vec3(f, f, 0);
              if(f>=0.0) col.r = f;
               else col.b = -f; 

                fragColor = vec4(col, 1.0);
}
