float pi=3.14159;
float ringMultiplier=20.0;
float t=1.0;
vec3 speed=vec3(2.0,2.2,3.0);
vec3 speedIncrement = vec3(1.1,1.1,1.1);

bool keyToggle(int ascii) {
	return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}

float spiral(vec2 m) {
	float r = length(m);
	float a = atan(m.y, m.x);
	float v = sin(100.*(sqrt(r)-0.02*a-.3*iTime))/2.;
	//return clamp(v,0.,1.);
    return v+0.5;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    if (keyToggle(107)) speed=speed+speedIncrement; //+
    if (keyToggle(109)) speed=speed-speedIncrement; //-
    float aspect=iResolution.x/iResolution.y;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,0,0));
    //col=uv.xyx;
    col.z=iMouse.x/iResolution.x;
    vec2 distance = vec2(0,0);
    distance.x=uv.x-iMouse.x/iResolution.x;
    distance.y=uv.y-iMouse.y/iResolution.y;
    distance.x=distance.x*aspect;
    //distance=abs(distance);
    float d = sqrt(distance.x*distance.x+distance.y*distance.y);

    d=spiral(distance);

    col=vec3(sin((d*pi*ringMultiplier+iTime*speed.x)),cos((d*pi*ringMultiplier+iTime*speed.y)),cos((-d*pi*ringMultiplier+iTime*speed.z)));
    col=vec3(sin(d*pi+iTime*speed.x),cos(d*pi+iTime*speed.y),cos(-d*pi+iTime*speed.z));
    // Output to screen
    fragColor = vec4(col,1.0);
    //fragColor = vec4(d,d,d,1.0);
}
