float drawsquare(in vec2 st){
    float thickness = abs(sin(iTime/2.0));
    vec2 bl = step(vec2(thickness),st);
    float pct = bl.x * bl.y;
    vec2 tr = step(vec2(thickness), 1.0-st);
    pct *= tr.x * tr.y;
    return pct;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float trans = step(abs(sin(iTime)), uv.x);
    float trans2 = 1.0-step(abs(sin(iTime)), uv.x);
    float trans3 = step(abs(sin(iTime/2.0)), uv.y); // '/n' or '*n'- regulator of speed
    float trans4 = 1.0-step(abs(sin(clamp(0.0,3.1415/2.0,iTime))), uv.y); //use clamp to dismiss repeat
    vec3 trans_square = vec3(drawsquare(uv));

    vec4 tex = texture(iChannel0, uv);
    vec4 tex2 = texture(iChannel1, uv);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = mix(tex, tex2, vec4(vec3(trans2), 1.0));
}
