vec3 circle(vec2 uv, float left, float top, float radius){

    vec2 uv2 = vec2(uv.x - left *cos(iTime), uv.y - left *cos(iTime));
    vec3 col2 = vec3(12.*length(uv2 * radius));
    return vec3(smoothstep(1.,1.02,col2.x) - smoothstep(1.02,1.03,col2.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x = uv.x*iResolution.x/iResolution.y;
    // Time varying pixel color
    uv = uv -0.5 + 0.5*cos(iTime);
    vec3 col = vec3(10.*length(uv));

    col = vec3(smoothstep(.9,1.02,col.x) - smoothstep(1.02,1.03,col.x));

    uv = uv -0.5 + 0.5*cos(iTime);
    vec3 col2 = vec3(12.*length(uv *.2));
    col += vec3(smoothstep(1.,1.02,col2.x) - smoothstep(1.02,1.03,col2.x));


    col += circle(uv, 0.5, 0.5,0.2);
    col += circle(uv, 0.2, 0.2,0.6);
    col += circle(uv, 0.1, 0.1,0.1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
