int NUM=4;
float PI = 3.1415926535;
float DIR = 1.0;  //Should be 1 or -1
float SPEED = 0.55;  //Speed of the rotation in radians per second
vec2 POS = vec2(0.5, 0.25);
float ICON_WIDTH = 0.07;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1.0, 1.0, 1.0);

  	vec3 OutColor = vec3(0.0, 0.0, 0.0);
    float r = 0.01;

    for(int i=0;i<NUM;i++)
    {
        float angle = iTime * PI * SPEED + (float(i)*2.0*PI/float(NUM));
        vec2 c1 = vec2(POS.x + DIR * sin(angle)*ICON_WIDTH, POS.y);
        OutColor +=  (1.0 - (col  * smoothstep(r, r+0.001, length(uv - c1)))) * ((-cos(angle)*0.5)+0.5);
    }

    // Output to screen
    fragColor = vec4(OutColor,1.0);
}
