float force = 0.5;
float thickness = 0.1;
vec2 origin = vec2(0.0, 0.5);
vec2 target = vec2(0.0, 0.5);

//distance from C to a line defined by two points: A and B
float distance_to_line(vec2 C, vec2 A, vec2 B) {
    float k = (B.y - A.y) / (B.x - A.x);
    float t = B.y - k * B.x;

    return abs((k * C.x - C.y + t) / sqrt(k * k + 1.0));
}

//closest point to C on a line defined by A and B
vec2 closest_point(vec2 C, vec2 A, vec2 B){
    float k = (B.y - A.y) / (B.x - A.x);
    float t = B.y - k * B.x;

    float x = (-1.0 * (-C.x - k * C.y) - k * t) / (k * k + 1.0);
    float y = (k * (C.x + k * C.y) + t) / (k * k + 1.0);
    return vec2(x, y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    origin.x = sin(iTime * 4.0 - 12.3);
    target.x = sin(iTime * 4.0);


    float d_line = distance_to_line(uv, origin, target);
    float d_origin = distance(uv, origin);
    float d_target = distance(uv, target);

    //fragment is close to origin or target
    float mask = 1.0 - smoothstep(0.0, thickness, 
        min(d_origin, d_target));

    //fragment is close to the segment of a line between origin and target
    vec2 P = closest_point(uv, origin, target);
    if(P.x >= min(origin.x, target.x) && P.x <= max(origin.x, target.x) && 
    P.y <= max(origin.y, target.y) && P.y >= min(origin.y, target.y)) 
    {
        mask = 1.0 - smoothstep(0.0, thickness, d_line);     
    }

    vec2 disp = (uv - P) * force * mask;

    //fragColor.rgb = vec3(mask);
    fragColor = texture(iChannel0, uv - disp);
}
