void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float depth = 0.5;
    float center_distance = length(position);
    float circle = center_distance - 1.0f;
    float core = center_distance - depth;
	float ring = circle * core * -300.0;
	fragColor = vec4(ring, ring, ring, 1.0);
}
