// Signed distance and line segment

// Segment:              https://www.shadertoy.com/view/3tdSDj
// and many more here:   https://iquilezles.org/articles/distfunctions2d

float line_segment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 pos = (fragCoord - iResolution.xy * .5) / iResolution.y;
    float zoom = 2.5;
    pos *= zoom;

	vec2 v1d1 = vec2(0.1, 0.1);
	vec2 v2d1= vec2(0.7, 0.7);
    float d1 = smoothstep(0.007, 0.008, line_segment(pos, v1d1, v2d1));

    vec2 v1d2 = vec2(0.5, 0.0);
	vec2 v2d2 = vec2(0.7, 0.0);
    float d2 = smoothstep(0.007, 0.008, line_segment(pos, v1d2, v2d2));

	vec3 color = vec3(min(d1, d2));
	fragColor = vec4(color, 1.);
}
