// checkered board
// imitated https://www.shadertoy.com/view/ftXcDn

#define PI 3.14159

mat2 rot(float a){
    float s=sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -0.5*iResolution.xy ) / iResolution.y;
    uv *= 10.;
    vec2 fuv = fract(uv);
    float f = 0.;
    //f += step(length(fuv-0.75), 0.25) + step(length(fuv-0.25), 0.25);
    f += step(0.5, fuv.x) * step(0.5, fuv.y) + step(fuv.x, 0.5) * step(fuv.y, 0.5);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(f,f+2.,f+4.));

    uv *= rot(PI/4.+iTime*0.5);
    uv *= max(abs(sin(iTime*0.5)*0.2), 0.05);// zoom
    uv += sin(iTime);
    fuv = fract(uv);
    float f2 = step(0.5, fuv.x) * step(0.5, fuv.y) + step(fuv.x, 0.5) * step(fuv.y, 0.5);

    vec3 col2 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(f2,f2+2.,f2+4.));
    col = mix(col, col2, f2);

    fragColor.rgb = col;
}
