///modificado por jorge2017a2

//change these constants to get different patterns!
#define c2 0.05

#define cB1 0.0
#define cB2 0.01
#define cB3 0.05

#define c1 vec4(3.0+c2,2.5+c2,1.5,0)
#define cA1 vec4(3.0+cB1,2.5+cB1,1.5,0)
#define cA2 vec4(3.0+cB2,2.5+cB2,1.5,0)
#define cA3 vec4(3.0+cB3,2.5+cB3,1.5,0)


//----------------------------------------------------------------------------------------
//  3 out, 1 in...
vec3 hash31(float p)
{//from David Hoskin's "Hash without sine"
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec2 triangle_wave(vec2 a,float scale)
{ return abs(fract((a+c1.xy)*scale)-.5);}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 4.5/8.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    float tfijo=8.67;
    float ti=iTime;

    uv += vec2(18.67/2.0+ti,18.67/3.0+ti*0.5)/t1/8.0; //antes
    float t2 =tfijo;

    vec3 random1 = (hash31(3.+t2)-.5)/12.;
    vec3 random2 = (hash31(4.+t2)-.5)/12.;

    float offset = .5;
    float scale2 = 1.5;

    vec2 va1;
    vec2 va2;

    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {
            for(int k = 0; k < 3; k++){
                uv /= -scale2;
                uv.yx = triangle_wave(uv.yx-offset,scale)+triangle_wave(uv,scale);
                va1=vec2(random1[k]*random1[0],random2[k]);
                va2=vec2(random1[k],random2[k]);
                uv +=mix( va1, va2, sin(ti));
            }
            scale /= 1.+(scale2)*col.x/(8.);
            scale2 -= (col.x-1.)/(4.);
            col[c] = abs((uv.x)-(uv.y));
        }
	}

    fragColor = vec4(vec3(col*2.),1.0);

}
